mapper.select:
select count(*) 
from comments3
where board_num = #{board_num}

SELECT *
FROM (
    SELECT ROWNUM rnum, c.*
    FROM (
        SELECT num, id, content, reg_date, board_num
        FROM comments3
        WHERE board_num = #{board_num}
        ORDER BY reg_date desc
    ) c
    WHERE ROWNUM <= #{end}
)
WHERE rnum between #{start} and #{end}

mapper.update:
update comments3
set	content=#{content}
where num=#{num}
